import { hotSearchAPI, searchResultAPI } from "@/api";
import searchGjz from '../../components/searchGjz';
import SearchJg from '../../components/searchJg.vue';
export default {
  data() {
    return {
      value: '',
      resultList: [],
      //搜索结果
      hotArr: [] // 热搜关键字

    };
  },

  async created() {
    const res = await hotSearchAPI();
    console.log(res);
    this.hotArr = res.data.result.hots;
  },

  components: {
    searchGjz,
    SearchJg
  },
  methods: {
    async getListFn() {
      return await searchResultAPI({
        keywords: this.value,
        limit: 20
      });
    },

    async fnres(val) {
      // 点击热搜关键词
      this.value = val; // 选中的关键词显示到搜索框

      const res = await this.getListFn();
      console.log(res);
      this.resultList = res.data.result.songs;
    },

    async inputFn() {
      if (this.value.length === 0) {
        this.resultList = [];
        return;
      } else {
        const res = await this.getListFn();
        console.log(res);
        this.resultList = res.data.result.songs;
      }
    }

  }
};
