import Vue from 'vue'
import App from './App.vue'
import router from '@/router'
import { recommendMusicAPI } from '@/api/index'
Vue.config.productionTip = false

import { Tabbar, TabbarItem, NavBar, } from 'vant';
Vue.use(Tabbar);
Vue.use(TabbarItem);
Vue.use(NavBar);
// Vue.use(Toast);


new Vue({
  render: h => h(App),
  router
}).$mount('#app')


//歌单请求
async function myFn(){
  const res = await recommendMusicAPI({limit: 6});
  console.log(res);
}
myFn();
